option.value:
use crate::cpu::Cpu;

#[test]
fn test_asl_accumulator() {
    let mut cpu = Cpu::new();
    cpu.processor_status = 0b00100000;
    cpu.accumulator = 0b1000_0001;
    cpu.mem_write(0x0, 0x0A); // ASL Accumulator
    cpu.step();
    assert_eq!(cpu.accumulator, 0b0000_0010);
    assert!(cpu.get_carry_flag());
    assert!(!cpu.get_zero_flag());
    assert!(!cpu.get_negative_flag());
}

#[test]
fn test_asl_zeropage() {
    let mut cpu = Cpu::new();
    cpu.processor_status = 0b00100000;
    cpu.mem_write(0x0, 0x06); // ASL ZeroPage
    cpu.mem_write(0x1, 0x10);
    cpu.mem_write(0x10, 0b0100_0000);
    cpu.step();
    assert_eq!(cpu.mem_read(0x10), 0b1000_0000);
    assert!(!cpu.get_carry_flag());
    assert!(!cpu.get_zero_flag());
    assert!(cpu.get_negative_flag());
}

#[test]
fn test_asl_zeropagex() {
    let mut cpu = Cpu::new();
    cpu.processor_status = 0b00100000;
    cpu.x_register = 0x01;
    cpu.mem_write(0x0, 0x16); // ASL ZeroPageX
    cpu.mem_write(0x1, 0x20);
    cpu.mem_write(0x21, 0b1111_1111);
    cpu.step();
    assert_eq!(cpu.mem_read(0x21), 0b1111_1110);
    assert!(cpu.get_carry_flag());
    assert!(!cpu.get_zero_flag());
    assert!(cpu.get_negative_flag());
}

#[test]
fn test_asl_absolute() {
    let mut cpu = Cpu::new();
    cpu.processor_status = 0b00100000;
    cpu.mem_write(0x0, 0x0E); // ASL Absolute
    cpu.mem_write_u16(0x1, 0x1234);
    cpu.mem_write(0x1234, 0b0000_0001);
    cpu.step();
    assert_eq!(cpu.mem_read(0x1234), 0b0000_0010);
    assert!(!cpu.get_carry_flag());
    assert!(!cpu.get_zero_flag());
    assert!(!cpu.get_negative_flag());
}

#[test]
fn test_asl_absolutex() {
    let mut cpu = Cpu::new();
    cpu.processor_status = 0b00100000;
    cpu.x_register = 0x01;
    cpu.mem_write(0x0, 0x1E); // ASL AbsoluteX
    cpu.mem_write_u16(0x1, 0x2000);
    cpu.mem_write(0x2001, 0b1000_0000);
    cpu.step();
    assert_eq!(cpu.mem_read(0x2001), 0);
    assert!(cpu.get_carry_flag());
    assert!(cpu.get_zero_flag());
    assert!(!cpu.get_negative_flag());
}

